     * @param _uniflags         List of booleans. True means univ3 jar
     *
     */
    function updateJars(
        address[] calldata _jars,
        bool[] calldata _values,
        bool[] calldata _uniflags
    ) external override {
        controller.onlyGovernanceOrEmergency();
        for (uint256 i = 0; i < _jars.length; i++) {
            if (_values[i]) {
                _addJar(_jars[i], _uniflags[i]);
            } else {
                _removeJar(_jars[i]);
            }
        }
    }

    function getAllJars() external view override returns (address[] memory) {
        return jarList;
    }

    function getAllGauges() external view override returns (address[] memory) {
        return gaugeList;
    }

    function getJarStrategy(address _jar) external view override returns (address) {
        if (isUniv3[_jar]) {
            return pickleControlller.strategies(IJarUniV3(_jar).pool());
        }
        return address(0);
    }

    function getJarGauge(address _jar) external view override returns (address) {
        return gaugeProxy.gauges(_jar);
    }

    function getJarFromGauge(address _gauge) external view override returns (address) {
        return gaugeToJar[_gauge];
    }

    /* ============ Internal Functions ============ */

    function _addJar(address _jar, bool _univ3) private {
        uint256 totalSupply = IJar(_jar).totalSupply();
        if (totalSupply > 0) {
            jarList.push(_jar);
            jars[_jar] = true;
            address gauge = gaugeProxy.gauges(_jar);
            gaugeToJar[gauge] = _jar;
            gaugeList.push(gauge);
            isUniv3[_jar] = _univ3;
        }
    }

